import React, {useState, useEffect} from "react";
import { View, Text, StyleSheet, TouchableOpacity, ScrollView, Image, Alert } from "react-native";
import Constants from "expo-constants";
import { Feather as Icon } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import MapView, { Marker } from "react-native-maps";
import { SvgUri } from "react-native-svg";
import * as Location from 'expo-location';
import api from '../../services/api';


/*interface Point {
  id: number;
  name: string;
  image_url: string;
  lat: number;
  lgt: number;
};*/

interface Item {
  id: number;
  name: string;
  image_url: string;
};

const Points = () => { 

  const [items, setItems] = useState<Item[]>([]);  
 // const [selectedItems, setSelectedItems] = useState<number[]>([]);
//  const [initialPosition, setInitialPosition] = useState<[number, number]>([0,0]);
 // const [points, setPoints] = useState<Point[]>([]);

  const navigation = useNavigation();
/*
  useEffect(() => {
    async function loadPosition(){
      const {status} = await Location.requestPermissionsAsync();
      if(status !=='granted'){
        Alert.alert("Alerta", "Precisamos da permissão de localização");
        return;
      }
      const location = await Location.getCurrentPositionAsync();

      const { latitude, longitude} = location.coords;

      setInitialPosition([latitude, longitude]);
    }

    loadPosition();

  }, []);*/

  /*useEffect(() => {
    api.get('points', {
      params: {
        city: 'SP',
        uf: 'SP',
        items: [1,2]
      }
    }).then( response => {
      setPoints(response.data);
    }    );
  }, []);*/

  useEffect(() => {
    api.get('items').then(response => {
      console.log(response.data);
      
      setItems(response.data);
    });
  }, []);

  /*function handleSelectItem(id: number){
    const alreadySelected = selectedItems.findIndex(item => item === id);
    if(alreadySelected>=0){
      const filtereditems = selectedItems.filter(item => item !== id);
      setSelectedItems(filtereditems);
    } else{
    setSelectedItems([...selectedItems, id]);
    }
  }*/

  function handleBack() {
    navigation.goBack();
  }

  function handleToDetail() {
    navigation.navigate("Detail");
  }

  return (
    <>
      <View style={styles.container}>
      <TouchableOpacity onPress={handleBack}>
          <Icon name="arrow-left" size={20} color="#34cb79" />
        </TouchableOpacity>

        <Text style={styles.title}>Bem vindo.</Text>
        <Text style={styles.description}>
          Encontre no mapa um ponto de coleta.
        </Text>
        <View style={styles.mapContainer}>
        {initialPosition[0] !== 0 && (          <MapView
          loadingEnabled={initialPosition[0] ===0}
            style={styles.map}
            initialRegion={{
              latitude: initialPosition[0],
              longitude: initialPosition[1],
              longitudeDelta: 0.014,
              latitudeDelta: 0.014,
            }}
          >
            <Marker
              style={styles.mapMarker}
              onPress={handleToDetail}
              coordinate={{ latitude: -27.2092052, longitude: -14.640192 }}
            >
              <View style={styles.mapMarkerContainer}>
                <Image
                  style={styles.mapMarkerImage}
                  source={{
                    uri:
                      "https://www.iconexperience.com/_img/v_collection_png/256x256/shadow/market_stand.png",
                  }}
                />
                <Text style={styles.mapMarkerTitle}>Mercado</Text>
              </View>
            </Marker>
          </MapView>) }
        </View>
      </View>
      <View style={styles.itemsContainer}>
      <ScrollView
          horizontal
          contentContainerStyle={{ paddingHorizontal: 20 }}
          showsHorizontalScrollIndicator={false}
        >
        {items.map(item =>(
          <TouchableOpacity key={String(item.id)} 
          activeOpacity={0.6}
          >
            
            <Text style={styles.itemTitle}>{item.name}</Text>
          </TouchableOpacity>       
        )

        )}           
        </ScrollView>
      </View>
    </>
  );
};

export default Points;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 32,
    paddingTop: 20 + Constants.statusBarHeight,
  },

  title: {
    fontSize: 20,
    fontFamily: "Ubuntu_700Bold",
    marginTop: 24,
  },

  description: {
    color: "#6C6C80",
    fontSize: 16,
    marginTop: 4,
    fontFamily: "Roboto_400Regular",
  },

  mapContainer: {
    flex: 1,
    width: "100%",
    borderRadius: 10,
    overflow: "hidden",
    marginTop: 16,
  },

  map: {
    width: "100%",
    height: "100%",
  },

  mapMarker: {
    width: 90,
    height: 80,
  },

  mapMarkerContainer: {
    width: 90,
    height: 70,
    backgroundColor: "#34CB79",
    flexDirection: "column",
    borderRadius: 8,
    overflow: "hidden",
    alignItems: "center",
  },

  mapMarkerImage: {
    width: 90,
    height: 45,
    resizeMode: "cover",
  },

  mapMarkerTitle: {
    flex: 1,
    fontFamily: "Roboto_400Regular",
    color: "#FFF",
    fontSize: 13,
    lineHeight: 23,
  },

  itemsContainer: {
    flexDirection: "row",
    marginTop: 16,
    marginBottom: 32,
  },

  item: {
    backgroundColor: "#fff",
    borderWidth: 2,
    borderColor: "#eee",
    height: 120,
    width: 120,
    borderRadius: 8,
    paddingHorizontal: 16,
    paddingTop: 20,
    paddingBottom: 16,
    marginRight: 8,
    alignItems: "center",
    justifyContent: "space-between",

    textAlign: "center",
  },

  selectedItem: {
    borderColor: "#34CB79",
    borderWidth: 2,
  },

  itemTitle: {
    fontFamily: "Roboto_400Regular",
    textAlign: "center",
    fontSize: 13,
  },
});
